                    enddate: end,
                    desc: des, */}
                    arr[index] = {...arr[index], title: titleAchievement, date: dateAchievement, descAch: descriptionAchievement}
                    setArr([...arr])
    }
    const updateUserWork=(obj)=>{
        console.log(obj);
    }
    const updateUserAchieve=(titleAchievement,dateAchievement,descriptionAchievement,indexAchievement)=>{
        arrAc[indexAchievement] = {...arrAc[indexAchievement], title: titleAchievement, date: dateAchievement, descAch: descriptionAchievement}
        setArrAc([...arrAc])
        console.log(arrAc);



